mapper.select:
SELECT 

FROM ods_nginx_tourism a

WHERE a.id = #{id}

SELECT 

FROM ods_nginx_tourism a






		ORDER BY ${page.orderBy}

SELECT concat(#{reqDate},' ',time_point) as req_time,if(b.counts is null,0,b.counts) as counts FROM (SELECT time_point FROM time_point WHERE sec%600=0) a left join (SELECT app_name,req_time,sum(counts) as counts FROM ods_nginx_tourism WHERE app_name =#{appName} AND req_date=#{reqDate}
AND http_status >= 400
GROUP BY app_name,req_time) b on a.time_point=substr(req_time,12,5) order by req_time

SELECT server_ip, concat(#{reqDate},' ',time_point) as req_time,if(b.counts is null,0,b.counts) as counts FROM (SELECT time_point FROM time_point WHERE sec%600=0) a left join (SELECT server_ip,req_time,sum(counts) as counts FROM ods_nginx_tourism WHERE app_name =#{appName} AND req_date=#{reqDate} AND server_ip = #{serverIp}
AND http_status >= 400
GROUP BY server_ip,req_time) b on a.time_point=substr(req_time,12,5) order by req_time

SELECT 

FROM ods_nginx_tourism a






		ORDER BY ${page.orderBy}
