mapper.select:
SELECT 

FROM TRAVEL_QPS_URL a

WHERE a.id = #{id}

SELECT 

FROM TRAVEL_QPS_URL a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM TRAVEL_QPS_URL a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE TRAVEL_QPS_URL SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
